<template>
<section id="features">
	<div class="container global">
		
		<div class="grid gap-y-10 justify-center 
									md:gap-x-5 md:grid-cols-3
									lg:gap-x-12">

			<div 
				v-for="card in data.cards"
				:key="card.id"
				:class="[`text-brand-${card.color}`]">
				<div class="card">
					<div class="card-image" v-html="icons[card.img]" />
					<h3 class="card-title">{{ card.title }}</h3>
					<div class="card-content" v-html="card.content" />
				</div>
			</div>

		</div>


		<div class="mt-14 lg:mt-20">
			<div class="flex justify-center">
				<span @click="() => store.showModalContact(true)">
					<BtnCta type="submit">
						{{ data.cta_title }}
					</BtnCta>
				</span>
				
			</div>
		</div>

	</div>
</section>
</template>


<style lang="scss" scoped>
#features{
	@apply px-6 pb-20 -mt-20;
}

.card{
	@apply bg-white border border-gray-200 
						max-w-sm h-full px-8 pt-8 pb-14 rounded-3xl;
}

.card-image{
	@apply w-20 h-20 mx-auto flex items-center;

	svg{
		@apply w-full;
	}
}

.card-title{
	@apply text-lg leading-none font-bold mt-5;
}

.card-content{
	@apply leading-tight mt-4 lg:text-sm;
}
</style>